import React, { useState, useEffect } from 'react';
import './App.css';
import DcdListProducts from './components/DcdListProducts';
import DcdProductAdd from './components/DcdProductAdd';

function App() {
  const [Dcd, setDcd] = useState('Dcd'); // Thay đổi hvt thành Dcd
  const [msv, setMsv] = useState('2210900095');
  const [DcdProducts, setDcdProducts] = useState([ // Thay đổi hvt thành Dcd
    {
      DcdProductId: 'msv',
      DcdProductName: 'Dcd',
      DcdQuantity: 15,
      DcdPrice: 1000,
    },
    {
      DcdProductId: 'P002',
      DcdProductName: 'IPhone 12',
      DcdQuantity: 20,
      DcdPrice: 1250,
    },
    {
      DcdProductId: 'P003',
      DcdProductName: 'IPhone 13',
      DcdQuantity: 10,
      DcdPrice: 1500,
    },
  ]);

  useEffect(() => {
    document.title = `[Dcd-msv] - ${Dcd}`; // Thay đổi hvt thành Dcd
  }, [Dcd]);

  const handleAddProduct = (newProduct) => {
    setDcdProducts([...DcdProducts, newProduct]);
  };

  return (
    <div className="App">
      <h1>{`[Dcd-msv] - ${Dcd}`}</h1> // Thay đổi hvt thành Dcd
      <DcdListProducts products={DcdProducts} />
      <DcdProductAdd onAddProduct={handleAddProduct} />
    </div>
  );
}

export default App;
